                            { label: qsTr('右'), value: HusTabView.Position_Right }
                        ]
                    }

                    Row {
                        spacing: 10

                        HusText { text: qsTr('是否可新增') }

                        HusSwitch {
                            id: addableSwitch
                            checkedText: qsTr('是')
                            uncheckedText: qsTr('否')
                            anchors.verticalCenter: parent.verticalCenter
                        }
                    }

                    Row {
                        spacing: 10

                        HusText { text: qsTr('是否居中') }

                        HusSwitch {
                            id: isCenterSwitch
                            checkedText: qsTr('是')
                            uncheckedText: qsTr('否')
