                    }

                    HusTabView {
                        id: defaultTabView
                        width: parent.width
                        height: 200
                        defaultTabWidth: 40
                        tabPosition: positionRadio1.currentCheckedValue
                        tabSize: sizeSwitch.checked ? HusTabView.Size_Fixed : HusTabView.Size_Auto
                        tabAddable: addableSwitch.checked
                        tabCentered: isCenterSwitch.checked
                        addTabCallback:
                            () => {
                                append({
