                            radiusBg.all: 0
                            radiusBg.topRight: 5
                            radiusBg.bottomRight: 5
                            iconSource: HusIcon.PlusOutlined
                            onClicked: {
                                if (newPercent + 10 <= 100)
                                    newPercent += 10;
                            }
                        }
                    }
                }

