                    HusButtonBlock {
                        model: [
                            { label: 'Default', type: HusButton.Type_Default },
                            { label: 'Outlined', type: HusButton.Type_Outlined },
                            { label: 'Primary', type: HusButton.Type_Primary },
                            { label: 'Filled', type: HusButton.Type_Filled },
                            { label: 'Text', type: HusButton.Type_Text },
